INSERT INTO `cc_swords` VALUES (1,0,'<h2 style=\"margin: 0px 0px 5px; padding: 0px; font-size: 3.6rem; line-height: normal; text-align: center;\">香叶核心价值</h2><p class=\"news-pp\" style=\"margin-top: 0px; margin-bottom: 15px; padding: 0px; text-align: center; line-height: 1.8;\"><span class=\"sp1\" style=\"font-size: 20px; color: rgb(255, 255, 0); font-weight: bold;\">使命</span><br/><span class=\"sp2\" style=\"font-size: 16px; line-height: 1.8;\">创造喜悦人生</span></p><p class=\"news-pp\" style=\"margin-top: 0px; margin-bottom: 15px; padding: 0px; text-align: center; line-height: 1.8;\"><span class=\"sp1\" style=\"font-size: 20px; color: rgb(255, 255, 0); font-weight: bold;\">愿景</span><br/><span class=\"sp2\" style=\"font-size: 16px; line-height: 1.8;\">全球每一个城市 每一条街 都有我们 是顾客最爱用餐地 因为我们 人生喜悦</span></p><p class=\"news-pp\" style=\"margin-top: 0px; margin-bottom: 15px; padding: 0px; text-align: center; line-height: 1.8;\"><span class=\"sp1\" style=\"font-size: 20px; color: rgb(255, 255, 0); font-weight: bold;\">承诺</span><br/><span class=\"sp2\" style=\"font-size: 16px; line-height: 1.8;\">我们承诺，心怀使命和人生召唤，坚守核心价值观，荣耀承诺，践行工匠精神，实现企业愿景，一切服务顾客；</span></p><p class=\"news-pp\" style=\"margin-top: 0px; margin-bottom: 15px; padding: 0px; text-align: center; line-height: 1.8;\"><span class=\"sp1\" style=\"font-size: 20px; color: rgb(255, 255, 0); font-weight: bold;\">核心价值观</span><br/><span class=\"sp2\" style=\"font-size: 16px; line-height: 1.8;\">真实 负责任 荣耀承诺</span></p><p><br/></p>',1513914771);

